DROP FUNCTION IF EXISTS "public"."u_login"("_username" varchar, "_password" varchar);
CREATE OR REPLACE FUNCTION "public"."u_login"("_username" varchar, "_password" varchar)
  RETURNS "pg_catalog"."int4" AS $BODY$
